import React, { useReducer, useState, useEffect } from 'react'
import { Interface } from 'readline';
import TextInputIndex from '../../smallercomponents/textinput'


type

const Register = () => {
    const [formData, setFormData] = useState(new userFormInput());
    const [submitting, setSubmitting] = useState(false);

    const handleSubmit = event => {
        event.preventDefault();
        setSubmitting(true);

        setTimeout(() => {
            setSubmitting(false);
        }, 3000);
    }

    const handleChange = event => {
        console.log(event.target)
        setFormData({
            username: event.target.username,
            password: event.target.password,
            passwordCopy: event.target.passwordCopy
        });
    }

    useEffect(() => {
        console.log(formData)
    }, [formData])

    return (
        <>
            <h1>Register Page</h1>
            {submitting &&
                <h1>Submitting...</h1>}
            <form onChange={handleChange}>
                <fieldset>
                    <TextInputIndex id="username" label="Enter a username" value={formData.username} ></TextInputIndex>
                    <TextInputIndex id="password" label="Enter a password" value={formData.password} ></TextInputIndex>
                    <TextInputIndex id="repassword" label="Re-enter password" value={formData.passwordCopy} ></TextInputIndex>
                    <button onSubmit={handleSubmit}>Submit form</button>
                </fieldset>
            </form>


        </>
    )
}

export default Register
