import React, { useReducer, useState } from 'react'
import TextInputIndex from '../../smallercomponents/textinput'

const formReducer = (state, event) => {
    return {
        ...state,
        [event.userName]: event.value
    }
}


const Register = () => {
    const [formData, setFormData] = useReducer(formReducer, {});
    const [submitting, setSubmitting] = useState(false);


    const handleSubmit = event => {
        event.preventDefault();
        setSubmitting(true);

        setTimeout(() => {
            setSubmitting(false);
        }, 3000);
    }

    const handleChange = event => {
        setFormData({
            userName: event.target.userName,
        });
    }

    return (
        <>
            <h1>Register Page</h1>
            <form>
                <fieldset>
                    <TextInputIndex id="username" label="Enter a username" onChange={handleChange} value={formData.userName} ></TextInputIndex>
                    <TextInputIndex id="password" label="Enter a password" onChange={handleChange} value={formData.passWord} ></TextInputIndex>
                    <TextInputIndex id="repassword" label="Re-enter password" onChange={handleChange} value={formData.passWordCopy} ></TextInputIndex>
                    <button>Submit form</button>
                </fieldset>
            </form>


        </>
    )
}

export default Register
